(959, 'RÃ©sultats de l\'Ekiden de Paris', '<p>\r\n	Disput&eacute; le dimanche 4 novembre, l&#39;<a href=\"http://bases.athle.com/asp.net/liste.aspx?frmbase=resultats&amp;frmmode=1&amp;frmespace=0&amp;frmcompetition=206703\" target=\"_blank\"><span style=\"color:#0000ff;\">Ekiden Maif de Paris</span></a> a vu 2 &eacute;quipes avec des repr&eacute;sentants de Montgeron courir le relais d&#39;un parcours de plusieurs boucles (5km - 10km - 5km - 10km - 5km - 7,2km) au coeur de Paris.</p>\r\n<p>\r\n	Bravo &agrave; nos 2 &eacute;quipes :</p>\r\n<ul>\r\n	<li>\r\n		<strong>Les Gladiateurs de l&#39;ESM - 11e de la course Open en 2h24&#39;47&quot;</strong><br />\r\n		Scarf Lessaint - Louis Legros - Walid Marzouk - Nicolas Cabourdin - Lotfi Saheb - Geoffrey Delver<br />\r\n		&nbsp;</li>\r\n	<li>\r\n		<strong>Les Running Stones - 375e de la course Open en 3h20&#39;39&#39;</strong><br />\r\n		Karine Boyer - Myriam Al Aloocy - Sarah Boumediene - Prunelle Charignon - Mylene Lechevalier</li>\r\n</ul>\r\n', '2018-11-11 14:48:50', 1, 'Competitions', NULL, 3, 'uploads/ekiden-paris-2018.jpg', ''),
(960, 'AssemblÃ©e gÃ©nÃ©rale du club samedi 17 novembre', '<p>\r\n	<strong>L&#39;assembl&eacute;e g&eacute;n&eacute;rale du club aura lieu samedi 17 novembre &agrave; 18h30.&nbsp;</strong>Vous &ecirc;tes attendu nombreux !</p>\r\n<p>\r\n	Plus d&#39;informations au bureau du club...</p>\r\n', '2018-11-11 14:53:38', 1, 'Actu Club', NULL, 3, 'uploads/cr2.jpg', '');
